        userName: '',
        email: '',
        id: '',
        password: '',
        passwordConfirm: ''
    });

    const { userName, email, id, password, passwordConfirm } = inputValue;

    const handleInput = (event) => {
        const { name, value } = event.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
    };

    // 이름 적합성 검사
    const isVaildUserName = userName.length >= 2 && userName.length < 5;

    // 이메일 적합성 검사
    const isVaildEmail = email.includes('@') && email.includes('.');

    // 비밀번호 특수문자 검사를 위한 정규식
    const specialLetter = password.search(/[`~!@@#$%^&*|₩₩₩'₩";:₩/?]/gi);

    // 비밀번호 적합성 검사
    // 특수문자 1개 이상, 전체길이가 8 이상
    const isVaildPassword = password.length >= 8 && specialLetter >= 1;

    // 비밀번호 일치 적합성 검사
    const isVaildPasswordConfirm = password === passwordConfirm;

    // 버튼 활성화 조건
    const isVaildInput = userName.length >= 1 && email.length >= 1 && id.length >= 1 && password.length >= 1 && passwordConfirm.length >= 1;

